import React, { useEffect, useState } from "react"
import "./Posts.css";

<script src="https://cdn.jsdelivr.net/npm/contentstack@latest/dist/web/contentstack.min.js"></script>
function PostsCards() {
  const [contents, setContents] = useState([])
  const Stack = Contentstack.Stack({ "api_key": "blt19d69e3984ff963a", "delivery_token": "cs42701f5dfb9cd8f0636dff0a", "environment": "hd-prod" });

  const Query = Stack.ContentType('hd_food_blog').Query();

  const datas = () => {

    Query
      .limit(20)
      .toJSON()
      .find()
      .then(response => {
        return response
      })
      .then(data => {
        setContents(data)
      })
  }
  useEffect(() => {
    datas()
  }, [])

