import React from "react";
import Contentstack from 'contentstack';
import "./Posts.css";

const post=({
  type: "GET",
  url:
    "https://cdn.contentstack.io/v3/content_types/hd_food_blog/entries?environment=hd-prod",
  headers: {
    "Content-Type": "application/json",
    api_key: "blt19d69e3984ff963a",
    access_token: "cs42701f5dfb9cd8f0636dff0a"
  },
  dataType: "json",
  success: PostsCards,
  error: function() {
    alert("failed");
  }
});
function PostsCards(data){
  (".title").append(data.entries.food_image);
  (".description").append(data.enties.description);
  (".place").append(data.entries.address.title)
  ("href").append(data.entries.address.href)

};



 <script src="https://cdn.jsdelivr.net/npm/contentstack@latest/dist/web/contentstack.min.js"></script>

const Stack = Contentstack.Stack({ "api_key": "blt19d69e3984ff963a", "delivery_token": "cs42701f5dfb9cd8f0636dff0a", "environment": "hd-prod" });

const Query = Stack.ContentType('hd_food_blog').Query();

Query
   .where("title")
   .includeSchema()
   .includeCount()
   .toJSON()
   .find()
   .then(function success(result) {
      // result is array where -
      console.log(result)
      // result[0] => entry objects
      // result[result.length-1] => entry objects count included only when .includeCount() is queried.
      // result[1] => schema of the content type is included when .includeSchema() is queried.
  }, function error(err) {
     // err object
 });

