import React, { useState, useEffect } from "react";
import Contentstack from 'contentstack';
// import "./Posts.css";
<script src="https://cdn.jsdelivr.net/npm/contentstack@latest/dist/web/contentstack.min.js"></script>

function PostsCards() {

  const [posts, setPosts] = useState([])
  const [imgs, setImgs] = useState()

  const Stack = Contentstack.Stack({ "api_key": "blt19d69e3984ff963a", "delivery_token": "cs42701f5dfb9cd8f0636dff0a", "environment": "hd-prod" });
  const Query = Stack.ContentType('hd_food_blog').Query();
  const getAssets = async () => {
    const assets = Stack
      .Assets()
      .Query()
      .toJSON()
      .find()
      .then(function success(img) {
        console.log(img)
        setImgs(img)

      })
  }
  useEffect(() => {
    getAssets()
  }, [])

  const getContent = async () => {
    const data = Query
      .where("title")
      .includeCount()
      .toJSON()
      .find()
      .then(function success(result) {
        // for (let i = 0; i < result[0].length; i++) {
        //   const title = result[0][i].title;
        //   console.log(title);
        console.log(result)
        setPosts(result)
        // }
        // return result

      },
        function error(err) {
        });


  }
  useEffect(() => {
    getContent();
  }, [])
  const p = () => {
    for (let i = 0; i < posts[0].length; i++) {
      const title = posts[0][i].title;
      const food_image = posts[0][i].food_image;
      const description = posts[0][i].description;
      const address = posts[0][i].address;
      const address_link = posts[0][i].address_link;
      console.log(posts)
      console.log(title);
      console.log(food_image);
      console.log(description)
      console.log(address)
      console.log("t", title);
      return (<div className="box" key={posts[0][i].uid}>
        <h5 className="headings2">{title}</h5>
        <img className="image" src={food_image[0].url} alt="post" />
        <p>{description}</p><br></br>
        <div className="info">
          <h4 className="place" href={address_link}>- {address}</h4>
        </div>
      </div>)
    }
  }
  console.log("data", posts)
  return (
    <div id="blog">
      <h2 className="headings">BLOG</h2>
      <div className="row">
      {p()}{p()}
        {posts[0].map((post) => {
          return (
            <div className="box" key={post.uid}>
              <h5 className="headings2">{post.title}</h5>
              <img className="image" src={post.food_image[0].url} alt="post" />
              <p>{post.description}</p><br></br>
              <div className="info">
                <h4 className="place" href={post.address_link}>- {post.address}</h4>
              </div>
            </div>
          )
        })}
      </div>
    </div>
  );
}
export default PostsCards;

