import { AnyNode } from '@contentstack/utils/dist/types/json-to-html';
import { EntryEmbedable, EmbeddedItem } from '@contentstack/utils/dist/types/Models/embedded-object';
import { EntryNode } from '@contentstack/utils/dist/types/Models/json-rte-model';
import { Metadata } from '@contentstack/utils/dist/types/Models/metadata-model';
import Node from '@contentstack/utils/dist/types/nodes/node';
export type Next = (nodes: AnyNode[]) => string;
export type RenderNode = (node: Node, next: Next) => string;
export type RenderMark = (text: string) => string;
export type RenderItem = (item: EmbeddedItem | EntryNode, metadata: Metadata) => string;
export interface RenderOption {
    [embedType: string]: RenderNode | RenderMark | RenderItem | RenderContentType;
}
export interface RenderContentType {
    [contentTypeUid: string]: RenderItem;
}
export interface Option {
    entry: EntryEmbedable;
    renderOption?: RenderOption;
}
